mapper.select:
/* Board -    boardDetail */
SELECT
      b_num, b_name, b_title, b_content,
      TO_CHAR(b_date, 'YYYY-MM-DD HH24:MI:SS') AS b_date, readcnt
FROM spring_board
WHERE b_num = #{b_num}

mapper.update:
/* Board - readCntUpdate */
UPDATE spring_board
SET readcnt = readcnt + 1
WHERE b_num = #{b_num}

UPDATE spring_board 
SET b_title = #{b_title}, 
    b_content = #{b_content}, 
    b_date = sysdate

   	, b_pwd= #{b_pwd} 

WHERE b_num = #{b_num}
